package Lec1;

public class Assignment_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 9;
		x += 6;
		System.out.println(x);
	
		int p = 11;
		p -= 3;
		System.out.println(p);
		
		//int q = 3;
		p *= 7;
		System.out.println(p);
		
		p/=5;
		System.out.println(p);
		
		p %=3;
		System.out.println(p);

	}

}
